import {useState} from "react";

const Form = props => {
    const [userInput, setUserInput] = useState("")
    const handleChange = event => setUserInput(event.target.value);

    return (
        <>
            <form onSubmit={()=>props.setNote(userInput)} >
                <input 
                    type="text"
                    name="note"
                    placeholder="Enter Note"
                    value={""}
                    onChange={() => handleChange}
                />
            </form>
        </>
    );
};
export default Form;
